$(document).ready(function(){$(".homeSignup").hide();$(".homeSignin").hide();var e=$(window).height();e-=75;$(".homeTop").css("height",e);$(".homeMain .circle").mouseover(function(){newSrc=$(this).attr("src").replace(".png","-hover.png");$(this).attr("src",newSrc)});$(".homeMain .circle").mouseout(function(){newSrc=$(this).attr("src").replace("-hover.png",".png");$(this).attr("src",newSrc)});$(document).on("click",".getStarted",function(){$(".homeSignup").show();$(".homeMain").hide()});$(document).on("click",".haveAccount",function(){$(".homeSignin").show();$(".homeMain").hide();$(".homeSignup").hide()});$(document).on("click",".dontHaveAccount",function(){$(".homeSignin").hide();$(".homeMain").hide();$(".homeSignup").show()});$(document).on("click",".learnMore",function(){$(".homeSignin").hide();$(".homeMain").show();$(".homeSignup").hide()})});